SELECT file_seq, ref_seq, ref_type, original_file_nm, store_file_nm, file_size
FROM tb_file
WHERE ref_seq = #{refSeq}
AND ref_type = #{refType}

SELECT file_seq, ref_seq, ref_type, original_file_nm, store_file_nm, file_path, file_size, download_auth, delete_auth
FROM tb_file
WHERE file_seq = #{fileSeq}
